import React,{useEffect,useState} from 'react'
import { useProductContext } from '../../context/product-context'
import { url } from '../../utils/utils'

import Stars from '../Stars/Stars'
import {FaArrowCircleRight,FaClock,FaUserShield} from 'react-icons/fa'

 const SingleProductPage = () => {
    
    const {fetchgivenproduct,single_product,
         //product name
     product_name,
     product_tags,
        labelfor_1080p,
    pricefor_1080p,
    currencycode_1080p,
    currencysymbol_1080p,
    currencyformat_1080p,
    oldprice_1080p,
    oldprice_currencycode_1080p,
    oldprice_currencysymbol_1080p,
    oldprice_currencyformat_1080p,
    labelfor_4k,
    pricefor_4k,
    currencycode_4k,
    currencysymbol_4k,
    currencyformat_4k,
    oldprice_4k,
    oldprice_currencycode_4k,
    oldprice_currencysymbol_4k,
    oldprice_currencyformat_4k,

    //for battery_accessories
    labelfor_battery_accessory,
    pricefor_battery_accessory,
    currencycode_battery_accessory,
    currencysymbol_battery_accessory,
    currencyformat_battery_accessory,
    oldprice_battery_accessory,
    oldprice_currencycode_battery_accessory,
    oldprice_currencysymbol_battery_accessory,
    oldprice_currencyformat_battery_accessory,

    //for shipping
     get_shipping,
     shipping_props,
     ready_to_ship,
     in_stock,
     fast_dispatch,

     //quantity
     quantity_for_1080p,
     quantity_for_4k,
     quantity_for_battery,
     //totalprice
     total_price,
     //discount,review and count
     get_discount,
     discount_amount,
     discount_end_date,
     get_review,
     get_review_count,
     get_rating,
     get_total_buyers,
     //quantity
     quantity,
} = useProductContext()
   //console.log(single_product);

   const [quantityitem,setQuantityitem] = useState(0)


    useEffect(() => {
        fetchgivenproduct(`${url}`)
     }, [url])

     const increment = () =>{
         setQuantityitem(quantityitem+1)
     }

     const decrement = () =>{
         setQuantityitem(quantityitem-1)
             if(quantityitem <= 0){
                 return quantityitem = 1
             }
     }

    //  const {gettags,getcount,getrating,
    //     readytoship,instock,fastdispatch,
    //     discountamount,discountenddate,
    //     getoptions,getname,getreviews,
    //     gettotalbuyers,labelfor1080p,pricefor1080p,
    //     currencycodefor1080p,currencysymbolfor1080p,currencyformatfor1080p,
    //     oldpricecurrencycodefor1080p,oldpricecurrencysymbolfor1080p,oldpricecurrencyformatfor1080p,
    //     oldpricevaluefor1080p,labelfor4k,pricevaluefor4k,currencycodefor4k,
    //          currencysymbolfor4k,currencyformatfor4k,
    //          oldpricecurrencycodefor4k,
    //          oldpricevaluefor4k,oldpricecurrencyformatfor4k,oldpricecurrencysymbolfor4k,
    //          labelforbatteryaccess,pricevalueforbatteryaccess,currencycodeforbatteryaccess,
    //          currencysymbolforbatteryaccess,currencyformatforbatteryaccess,oldpricevalueforbatteryaccess,
    //          oldpricecurrencycodeforbatteryaccess,oldpricecurrencysymbolforbatteryaccess,oldpricecurrencyformatforbatteryaccess
    // } = single_product


            return (
        <div>
           <h4>{ready_to_ship && "Ready To Ship"}</h4>
        <h4>{in_stock && "In Stock"}</h4>
        <h4>{fast_dispatch && "Fast Dispatch"}</h4>
          <h3>This is {product_name}  <span>{product_tags}</span></h3>
                <Stars rating={get_rating}
                reviews={get_review}
                count={get_review_count}
                totalbuyers={get_total_buyers}/>
            <hr />
            <h3>R 78.50 - R 895.31 / Option 2 Options(Min.Order)</h3>
            <hr />
            <h3>March Expo  <FaArrowCircleRight />Free shipping (up to $40)  <FaArrowCircleRight />On time-delivery guaranteed</h3>
        <h3>{discount_amount} OFF Discount ends in      <FaClock /> {discount_end_date} </h3>
        <div>
                <ul> 
                    <h3>Options</h3>
                    <li>{labelfor_1080p}  {currencysymbol_1080p}   {pricefor_1080p}  
                                    <button value="1080p" onClick={increment}>-</button>
                                   Quantity : {quantityitem}
                                    <button>+</button>
                    </li>

                    <li>{labelfor_4k}  {currencysymbol_4k}   {pricefor_4k}  
                                    <button value="4k">-</button>
                                    Quantity : {quantity_for_4k}
                                    <button>+</button>
                    </li>

                    <li>{labelfor_battery_accessory}  {currencysymbol_battery_accessory}   {pricefor_battery_accessory}  
                                    <button value="battery_accessories">-</button>
                                    Quantity : {quantity_for_battery}
                                    <button>+</button>
                    </li>
                </ul>
                <h2>Total price is {total_price}</h2>
        </div>
        <div>
            <h3>Here comes the option columns</h3>
            <h3><FaUserShield />  Trade Assurance protects your Alibaba.com orders</h3>
            <h3>Alibaba.com Logistics  Inspection Solutions</h3>
        </div>
               
        </div>
    )
}


export default SingleProductPage
