import React,{useState} from 'react'
import './Product.styles.css'
import { useProductContext } from '../../context/product-context'
import Spinner from '../Spinner/Spinner'

const Product = (props) =>{

const { products_loading } = useProductContext()

    const [less,SetLess] = useState(true)

    const showless = less ? props.description.slice(0,103) : props.description

    const setdescription = () =>{
        SetLess(!less)
    }

    if(products_loading){
        return <Spinner />
    }

    return(
        <div className="product-article" key={props.id}>
        <h1>{props.category} - {props.rating.rate}‚≠ê</h1>
        <img className="image-view" src={props.image} alt={props.category}/>
        <h2 className="title-view">{props.title} from $ {props.price} onwards</h2>
        <h3>{showless}</h3>
        <span className="buttons" onClick={setdescription}>{less ? "Read More" : "Show Less"}</span>
        </div>
    )
}

export default Product
