    const { products,
    products_error,
    products_loading } = useProductContext()

    if(products_loading){
        return <Spinner />
    }

    if(products_error){
        return <ErrorPage />
    }

      const transform = {products}
      const arraytransform = Object.entries(transform)
      console.log(arraytransform);



    return(
        <div>
            <h2 className="heading-title">ðŸ‘‡ View the high Rated products here ðŸ‘‡</h2>
            <div className="container">
                <ul>
                    <li>
                        {
                             products.map((product)=>{
                                return <Product 
