import React from 'react'
import Spinner from '../Spinner/Spinner'
import ErrorPage from '../ErrorPage/ErrorPage'
import Product from '../Product/Product'

import { useProductContext } from '../../context/product-context'

const ProductsPage = () =>{

    const { products,
    products_error,
    products_loading } = useProductContext()

    if(products_loading){
        return <Spinner />
    }

    if(products_error){
        return <ErrorPage />
    }

    const transform = {products}
    const arraytransform = Object.entries(transform)
    console.log(arraytransform);

    const filtereditems = products.map((prod)=>
    prod.rating.rate >= 4 )

    return(
        <div>
            <div>
                <h2>View the high Rated products here</h2>
            </div>
            <div>
                {
                    products.map((product)=>{
                        return <Product key={product.id} {...product} 
                        itemsList ={filtereditems}/>
                    })
                }
            </div>
        </div>
    )
}


export default ProductsPage
