        // useEffect(()=>{
        //     fetchproducts(url)
        // },[url])

        if(single_product_begin){
            return <Spinner />
        }

        if(single_product_error){
            return <ErrorPage />
        }

    //    const {
    //     id:success,
    //     product,options,old_price,battery_accessories,discount,gallery,shipping,review
    //   } = single_product

    return(
        <div>
            <h2>The product details are</h2>
            <div>
               <SingleProductPage product={single_product}/>
            </div>
        </div>
    )
}

export default Task
