import React,{useEffect, useState} from 'react'
import { useProductContext } from '../../context/product-context'
import { singleproduct,url } from '../../utils/utils'
import Spinner from '../Spinner/Spinner'
import ErrorPage from '../ErrorPage/ErrorPage'
import SingleProductPage from '../SingleProductPage.js/SingleProductPage'

const Task =() =>{

    const {single_product,single_product_begin,
        single_product_error,
        fetchgivenproduct
        } = useProductContext()

        const [product,setProduct] = useState(singleproduct)

        useEffect(()=>{
            fetchgivenproduct(url)
            console.log(fetchgivenproduct(url));
        },[url])
    //  const transform = {single_product}
    //    const arraytransform = Object.entries(transform)
    //    console.log(arraytransform);

        if(single_product_begin){
            return <Spinner />
        }

        if(single_product_error){
            return <ErrorPage />
        }

       

    return(
        <div>
            <h2>The product details are</h2>
            {
                product.map((prod,index)=>{
                    return <SingleProductPage key={index} {...prod}/>
                })
            }
        </div>
    )
}

export default Task
