import React from 'react'
import { Route } from 'react-router'
import { useUserContext } from '../context/user-context'
import SingleProductPage from '../components/SingleProductPage.js/SingleProductPage'


const SignIn = () =>{

    const {loginWithRedirect,myUser,logout} = useUserContext()

        return (
        <div>
            <button onClick={loginWithRedirect}>Login here</button>
             {
                myUser ? <Route to="/task" component={SingleProductPage}/> : null
            }
            <h2>You can sign in here</h2>
        </div>
    )
}


export default SignIn
