import React from 'react'
import './Product.styles.css'

const Product = ({id,title,description,rating,price,category,image}) =>{
    //console.log(props);

    

    return(
        <div className="product-container">
         <div className="single-product" key={id}>
             <h2>{category} - {rating.rate}‚≠ê</h2>
        <img src={image} alt={category} className="image-view"/>
        <h3>{title}</h3>
        <h3>{description}</h3>
        <h4>$ {price}</h4>
         </div>
        </div>
    )
}

export default Product
