import React,{useEffect,useState} from 'react'
import { useProductContext } from '../../context/product-context'
import { url } from '../../utils/utils'

import Stars from '../Stars/Stars'
import {FaArrowCircleRight,FaClock,FaUserShield} from 'react-icons/fa'


    

 const SingleProductPage = () => {

    const {fetchgivenproduct,single_product,
         //product name
     product_name,
     product_tags,
    labelfor_1080p,
    pricefor_1080p,
    currencycode_1080p,
    currencysymbol_1080p,
    currencyformat_1080p,
    oldprice_1080p,
    oldprice_currencycode_1080p,
    oldprice_currencysymbol_1080p,
    oldprice_currencyformat_1080p,
    labelfor_4k,
    pricefor_4k,
    currencycode_4k,
    currencysymbol_4k,
    currencyformat_4k,
    oldprice_4k,
    oldprice_currencycode_4k,
    oldprice_currencysymbol_4k,
    oldprice_currencyformat_4k,

    //for battery_accessories
    labelfor_battery_accessory,
    pricefor_battery_accessory,
    currencycode_battery_accessory,
    currencysymbol_battery_accessory,
    currencyformat_battery_accessory,
    oldprice_battery_accessory,
    oldprice_currencycode_battery_accessory,
    oldprice_currencysymbol_battery_accessory,
    oldprice_currencyformat_battery_accessory,

    //for shipping
    get_shipping,
    shipping_props,
    ready_to_ship,
    in_stock,
    fast_dispatch,

    //shipping method
    shipping_country,
    shipping_title,
    shippingtime_leadvalue,
    shippingtime_info,

    //shipping cost
    shipping_cost_value,
    shipping_currencycode,
    shipping_currencysymbol,
    shipping_currencyformat,


     //quantity
     quantity_for_1080p,
     quantity_for_4k,
     quantity_for_battery,
     //totalprice
     total_price,
     //discount,review and count
     get_discount,
     discount_amount,
     discount_end_date,
     get_review,
     get_review_count,
     get_rating,
     get_total_buyers,
} = useProductContext()
   //console.log(single_product);
   const pricefor1080p = 833.99
   const pricefor4k = 895.31
   const priceforbattery = 78.5

   const [quantityfor1080p,setQuantityfor1080p] = useState(0)
   const [quantityfor4k,setQuantityfor4k] = useState(0)
   const [quantityforbattery,setQuantityforbattery] = useState(0)


    useEffect(() => {
        fetchgivenproduct(`${url}`)
     }, [url])

     const incrementfor1080p = () =>{
        setQuantityfor1080p(quantityfor1080p + 1)
     }

     const decrementfor1080p = () =>{
        setQuantityfor1080p(quantityfor1080p - 1)
        if(quantityfor1080p <= 1){
            setQuantityfor1080p(1)
        }
    }

    const incrementfor4k = () =>{
        setQuantityfor4k(quantityfor4k + 1)
    }

    const decrementfor4k = () =>{
       setQuantityfor4k(quantityfor4k - 1)
       if(quantityfor4k <= 1){
           setQuantityfor4k(1)
       }
   }

   const incrementforbattery = () =>{
    setQuantityforbattery(quantityforbattery + 1)
}

const decrementforbattery = () =>{
   setQuantityforbattery(quantityforbattery - 1)
   if(quantityforbattery <= 1){
       setQuantityforbattery(1)
   }
}



            return (
        <div>
           <h4>{ready_to_ship && "Ready To Ship"}</h4>
        <h4>{in_stock && "In Stock"}</h4>
        <h4>{fast_dispatch && "Fast Dispatch"}</h4>
          <h3>This is {product_name}  <span>{product_tags}</span></h3>
                <Stars rating={get_rating}
                reviews={get_review}
                count={get_review_count}
                totalbuyers={get_total_buyers}/>
            <hr />
            <h3>R 78.50 - R 895.31 / Option 2 Options(Min.Order)</h3>
            <hr />
            <h3>March Expo  <FaArrowCircleRight />Free shipping (up to $40)  <FaArrowCircleRight />On time-delivery guaranteed</h3>
        <h3>{discount_amount} OFF Discount ends in      <FaClock /> {discount_end_date} </h3>
        <div>
                <ul> 
                    <h3>Options</h3>
                    <li>{labelfor_1080p}  {currencysymbol_1080p}   {pricefor_1080p}  
                                    <button value="1080p" onClick={decrementfor1080p}>-</button>
                                   Quantity : {quantityfor1080p}
                                    <button onClick={incrementfor1080p}>+</button>
                                    <span>Totalprice is {total_price}</span>
                     </li>

                    <li>{labelfor_4k}  {currencysymbol_4k}   {pricefor_4k}  
                                    <button value="4k" onClick={decrementfor4k}>-</button>
                                    Quantity : {quantityfor4k}
                                    <button onClick={incrementfor4k}>+</button>
                                    <span>Amount is {quantityfor4k * pricefor4k}</span>

                    </li>

                    <li>{labelfor_battery_accessory}  {currencysymbol_battery_accessory}   {pricefor_battery_accessory}  
                                    <button value="battery_accessories" onClick={decrementforbattery}>-</button>
                                    Quantity : {quantityforbattery}
                                    <button onClick={incrementforbattery}>+</button>
                                    <span>Amount is {quantityforbattery * priceforbattery}</span>

                    </li>
                </ul>
                <h2>Total price is {total_price}</h2>
        </div>
        <div>
            <h3>Here comes the option columns</h3>
            <h3><FaUserShield />  Trade Assurance protects your Alibaba.com orders</h3>
            <h3>Alibaba.com Logistics  Inspection Solutions</h3>
        </div>
        <article>
            Ship to {shipping_country}  {shipping_title}
            <span>Total amount is {shipping_currencysymbol} {shipping_cost_value}</span>
            <h2>Lead time {shippingtime_leadvalue}</h2>
            <h2>Shipping time {shippingtime_info}</h2>
        </article>
        <button>Login to purchase</button>
        <button>Contact the supplier</button>
        </div>
    )
}


export default SingleProductPage
