import React,{useState,useEffect} from 'react'

const CountDownList = () =>{

    const [time,setTime] = useState("")
    useEffect(()=>{
        let countdowndate = new Date("Nov 11,2021 06:20:00").getTime()
        //updating every second
        let updatesecond = setInterval(function(){
            //for today date and time
            let todaydate = new Date()
            //finding difference between now and countdays
            let distance = countdowndate - todaydate

            let days = Math.floor(distance / (1000 * 60 * 60 * 24));
            let hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
            let minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
            let seconds = Math.floor((distance % (1000 * 60)) / 1000);
            setTime(days + "days" + hours + "h" + minutes + "m" + seconds + "s")
        
            if(distance < 0){
                clearInterval(updatesecond)
                setTime("Redirecting to your website")
            }
        },1000)
    },[])

    return(
        <div style={style}>
            {time}
        </div>
    )
}

const style = {
    fontSize:"15px",
    textAlign:"center"

}

export default CountDownList
