import React,{useEffect,useState} from 'react'
import { useProductContext } from '../../context/product-context'
import { url } from '../../utils/utils'
import Spinner from '../Spinner/Spinner'
import ErrorPage from '../ErrorPage/ErrorPage'
import SingleProductPage from '../SingleProductPage.js/SingleProductPage'

const Task =() =>{

    const {single_product,
        single_product_begin,
        single_product_error,
        fetchgivenproduct
        } = useProductContext()

        const [singleProduct,setSingleProduct] = useState([])

        useEffect(()=>{
           const fetcher = fetch(fetchgivenproduct(url)).
           then(res=>console.log(res)).
           then(results=>results.data)
        },[])

        if(single_product_begin){
            return <Spinner />
        }

        if(single_product_error){
            return <ErrorPage />
        }

    //    const {
    //     id:success,
    //     product,options,old_price,battery_accessories,discount,gallery,shipping,review
    //   } = single_product

    return(
        <div>
            <h2>The product details are</h2>
            <div>
                {
                    single_product.map((product)=>{
                        return <SingleProductPage key={product.id} {...product} />
                    })
                }
            </div>
        </div>
    )
}

export default Task
