import React,{useState} from 'react'
import './Product.styles.css'
import { useProductContext } from '../../context/product-context'

const Product = ({id,title,description,rating,price,category,image}) =>{

const { products_loading } = useProductContext()

    const [less,SetLess] = useState(true)

    const showless = less ? description.slice(0,103) : description

    const setdescription = () =>{
        SetLess(!less)
    }

    if(products_loading){
        return <Spinner />
    }

    return(
        <div className="item">
            <article className="product-article" key={id}>
        <h2>{category} - {rating.rate}‚≠ê</h2>
        <img className="image-view" src={image} alt={category}/>
        <h3 className="title-view">{title} from $ {price} onwards</h3>
        <h3>{showless}</h3>
        <span className="buttons" onClick={setdescription}>{less ? "Read More" : "Show Less"}</span>
        </article>        
        </div>
    )
}

export default Product
