import React,{useEffect,useState} from 'react'
import { useProductContext } from '../../context/product-context'
import { singleproduct, url } from '../../utils/utils'

import Stars from '../Stars/Stars'
import {FaArrowCircleRight,FaClock,FaUserShield} from 'react-icons/fa'

 const SingleProductPage = () => {
    
    const {fetchgivenproduct,single_product} = useProductContext()
   //console.log(single_product);

    const [value,setValue] = useState(0)

    const increment = () =>{
      setValue(value+1)
    }

    const decrement =()=>{
        setValue(value-1)
        if(value <= 0){
            setValue(1)
        }
    }

    useEffect(() => {
        fetchgivenproduct(`${url}`)
     }, [url])


     const {gettags,getcount,getrating,
        readytoship,instock,fastdispatch,
        discountamount,discountenddate,
        getoptions,getname,getreviews,
        gettotalbuyers,labelfor1080p,pricefor1080p,
        currencycodefor1080p,currencysymbolfor1080p,currencyformatfor1080p,
        oldpricecurrencycodefor1080p,oldpricecurrencysymbolfor1080p,oldpricecurrencyformatfor1080p,
        oldpricevaluefor1080p,labelfor4k,pricevaluefor4k,currencycodefor4k,
             currencysymbolfor4k,currencyformatfor4k,
             oldpricecurrencycodefor4k,
             oldpricevaluefor4k,oldpricecurrencyformatfor4k,oldpricecurrencysymbolfor4k,
             labelforbatteryaccess,pricevalueforbatteryaccess,currencycodeforbatteryaccess,
             currencysymbolforbatteryaccess,currencyformatforbatteryaccess,oldpricevalueforbatteryaccess,
             oldpricecurrencycodeforbatteryaccess,oldpricecurrencysymbolforbatteryaccess,oldpricecurrencyformatforbatteryaccess
    } = single_product


            return (
        <div>
           <h4>{readytoship && "Ready To Ship"}</h4>
        <h4>{instock && "In Stock"}</h4>
        <h4>{fastdispatch && "Fast Dispatch"}</h4>
          <h3>This is {getname}  <span>{gettags}</span></h3>
                <Stars rating={getrating}
                reviews={getreviews}
                count={getcount}
                totalbuyers={gettotalbuyers}/>
            <hr />
            <h3>R 78.50 - R 895.31 / Option 2 Options(Min.Order)</h3>
            <hr />
            <h3>March Expo  <FaArrowCircleRight />Free shipping (up to $40)  <FaArrowCircleRight />On time-delivery guaranteed</h3>
        <h3>{discountamount}% OFF Discount ends in      <FaClock /> {discountenddate} </h3>
        <div>
                <ul> 
                    <h3>Options</h3>
                    <li>{labelfor1080p}  {currencysymbolfor1080p}   {pricefor1080p}  
                                    <button onClick={decrement}>-</button>
                                    {value}
                                    <button onClick={increment}>+</button>
                    </li>

                    <li>{labelfor4k}  {currencysymbolfor4k}   {pricevaluefor4k}  
                                    <button onClick={decrement}>-</button>
                                    {value}
                                    <button onClick={increment}>+</button>
                    </li>

                    <li>{labelforbatteryaccess}  {currencysymbolforbatteryaccess}   {pricevalueforbatteryaccess}  
                                    <button onClick={decrement}>-</button>
                                    {value}
                                    <button onClick={increment}>+</button>
                    </li>
                </ul>
        </div>
        <div>
            <h3>Here comes the option columns</h3>
            <h3><FaUserShield />  Trade Assurance protects your Alibaba.com orders</h3>
            <h3>Alibaba.com Logistics  Inspection Solutions</h3>
        </div>
               
        </div>
    )
}


export default SingleProductPage
