import React,{useEffect,useState} from 'react'
import { useProductContext } from '../../context/product-context'
import { url } from '../../utils/utils'

import Stars from '../Stars/Stars'
import {FaArrowCircleRight,FaClock,FaUserShield,FaCcVisa,FaCcMastercard,FaApplePay} from 'react-icons/fa'
import { useUserContext } from '../../context/user-context'

const pricefor1080p=833.99
const pricefor4k=895.31
const priceforbattery=78.5
    

 const SingleProductPage = () => {

    const {fetchgivenproduct,single_product,
         //product name
     product_name,
     product_tags,
    labelfor_1080p,
    pricefor_1080p,
    currencycode_1080p,
    currencysymbol_1080p,
    currencyformat_1080p,
    oldprice_1080p,
    oldprice_currencycode_1080p,
    oldprice_currencysymbol_1080p,
    oldprice_currencyformat_1080p,
    labelfor_4k,
    pricefor_4k,
    currencycode_4k,
    currencysymbol_4k,
    currencyformat_4k,
    oldprice_4k,
    oldprice_currencycode_4k,
    oldprice_currencysymbol_4k,
    oldprice_currencyformat_4k,

    //for battery_accessories
    labelfor_battery_accessory,
    pricefor_battery_accessory,
    currencycode_battery_accessory,
    currencysymbol_battery_accessory,
    currencyformat_battery_accessory,
    oldprice_battery_accessory,
    oldprice_currencycode_battery_accessory,
    oldprice_currencysymbol_battery_accessory,
    oldprice_currencyformat_battery_accessory,

    //for shipping
    get_shipping,
    shipping_props,
    ready_to_ship,
    in_stock,
    fast_dispatch,

    //shipping method
    shipping_country,
    shipping_title,
    shippingtime_leadvalue,
    shippingtime_info,

    //shipping cost
    shipping_cost_value,
    shipping_currencycode,
    shipping_currencysymbol,
    shipping_currencyformat,


     //quantity
     quantity_for_1080p,
     quantity_for_4k,
     quantity_for_battery,
     //totalprice
     total_price,
     //discount,review and count
     get_discount,
     discount_amount,
     discount_end_date,
     get_review,
     get_review_count,
     get_rating,
     get_total_buyers,
     productimage,
     increasequantity
} = useProductContext()

            const {loginWithRedirect} = useUserContext()

useEffect(() => {
    fetchgivenproduct(`${url}`)
 }, [url])
   //console.log(single_product);
   
const [for1080p,Setfor1080p] = useState(quantity_for_1080p)
const [for4k,Setfor4k] = useState(quantity_for_4k)
const [forbattery,Setforbattery] = useState(quantity_for_battery)

const incrementhandlerfor1080p = () =>{
    Setfor1080p(for1080p+1)
 }

 const decrementhandlerfor1080p = () =>{
     Setfor1080p(for1080p-1)
     if(for1080p <= 1){
         Setfor1080p(1)
     }
 }

const incrementhandlerfor4k = () =>{
    Setfor4k(for4k+1)
}

const decrementhandlerfor4k = () =>{
    Setfor4k(for4k-1)
    if(for4k <= 1){
        Setfor4k(1)
    }
}

const incrementhandlerforbattery = () =>{
    Setforbattery(forbattery+1)
}

const decrementhandlerforbattery = () =>{
    Setforbattery(forbattery-1)
    if(forbattery <= 1){
        Setforbattery(1)
    }
}
    



        return (
        <div>
           <h4>{ready_to_ship && "Ready To Ship"}</h4>
        <h4>{in_stock && "In Stock"}</h4>
        <h4>{fast_dispatch && "Fast Dispatch"}</h4>
          <h3>This is {product_name}  <span>{product_tags}</span></h3>
                <Stars rating={get_rating}
                reviews={get_review}
                count={get_review_count}
                totalbuyers={get_total_buyers}/>
            <hr />
            <h3>R 78.50 - R 895.31 / Option 2 Options(Min.Order)</h3>
            <hr />
            <h3>March Expo  <FaArrowCircleRight />Free shipping (up to $40)  <FaArrowCircleRight />On time-delivery guaranteed</h3>
        <h3>{discount_amount} OFF Discount ends in      <FaClock /> {discount_end_date} </h3>
        <div>
               <ul>
                   <li>
                       {labelfor_1080p}  {currencysymbol_1080p} {pricefor_1080p}
                       <button>-</button> {for1080p} <button onClick={incrementhandlerfor1080p}>+</button>
                   </li>
                   <li>
                       {labelfor_4k}  {currencysymbol_4k} {pricefor_4k}
                       <button onClick={decrementhandlerfor4k}>-</button> {for4k} <button onClick={incrementhandlerfor4k}>+</button>
                   </li>
                   <li>
                       {labelfor_battery_accessory}  {currencysymbol_battery_accessory} {pricefor_battery_accessory}
                       <button onClick={decrementhandlerforbattery}>-</button> {forbattery} <button onClick={incrementhandlerforbattery}>+</button>
                   </li>
               </ul>
               {/* <h3>Your total price is {shipping_currencysymbol} {Math.round(for1080p*pricefor1080p + for4k*pricefor4k + forbattery*priceforbattery)}</h3> */}
               {/* <h3>Your quantity is {for1080p + for4k + forbattery}</h3> */}
        </div>
        <div>
            <h3>Here comes the option columns</h3>
            <h3><FaUserShield />  Trade Assurance protects your Alibaba.com orders</h3>
            <h3>Alibaba.com Logistics  Inspection Solutions</h3>
            <h3>Payments can be done with <FaCcVisa /> <FaCcMastercard /> <FaApplePay /></h3>
        </div>
        <article>
            Ship to {shipping_country}  {shipping_title}
            <span>Total amount is {shipping_currencysymbol} {shipping_cost_value}</span>
            <h2>Lead time {shippingtime_leadvalue}</h2>
            <h2>Shipping time {shippingtime_info}</h2>
        </article>
        <button onClick={loginWithRedirect}>Login to purchase</button>
        <button>Contact the supplier</button>
        {/* <button>Checkout with {shipping_currencysymbol} {Math.round(for1080p*pricefor1080p + for4k*pricefor4k + forbattery*priceforbattery)}

        </button> */}
        <img src={productimage} alt="name" />
        </div>
    )
}


export default SingleProductPage
