import React,{useReducer,useContext,useEffect} from 'react'
import reducer from '../reducers/product-reducer'
import { url,homepageurl } from '../utils/utils'
import axios from 'axios'
import {
    PRODUCTS_BEGIN,
 PRODUCTS_SUCCESS,
 PRODUCTS_ERROR,
 SINGLE_PRODUCT_BEGIN,
  SINGLE_PRODUCT_SUCCESS,
 SINGLE_PRODUCT_ERROR,
 FEATURES_1080P,
 FEATURES_4K,
 FEATURES_BATTERY_ACCESSORIES,
 SHIPPING_DETAILS,
 GET_PRODUCT_NAME_AND_TAG,
 GET_RATING_DETAILS,
 INCREASE_QUANTITY,
 DECREASE_QUANTITY,
 TOTAL_PRICE,
 PRODUCT_IMAGE,
 CART_TOTALS,
 LABEL_FOR_1080P,
 PRICE_FOR_1080P,
 CURRENCYCODE_FOR_1080P,
 CURRENCYFORMAT_FOR_1080P,
 OLDPRICE_FOR_1080P,
 OLDPRICE_CURRENCYCODE_FOR_1080P,
 OLDPRICE_CURRENCYSYMBOL_FOR_1080P,
 OLDPRICE_CURRENCYFORMAT_FOR_1080P
} from '../actions/actions'


const initialState ={
    products:[],
    products_loading:false,
    products_success:false,
    products_error:false,
    single_product:{},
    single_product_loading:false,
    single_product_error:false,
    single_product_success:false,
    //1080p
    labelfor_1080p:'',
    pricefor_1080p:'',
    currencycode_1080p:'',
    currencysymbol_1080p:'',
    currencyformat_1080p:'',
    oldprice_1080p:'',
    oldprice_currencycode_1080p:'',
    oldprice_currencysymbol_1080p:'',
    oldprice_currencyformat_1080p:'',
    //4k
    labelfor_4k:'',
    pricefor_4k:'',
    currencycode_4k:'',
    currencysymbol_4k:'',
    currencyformat_4k:'',
    oldprice_4k:'',
    oldprice_currencycode_4k:'',
    oldprice_currencysymbol_4k:'',
    oldprice_currencyformat_4k:'',

    //for battery_accessories
    labelfor_battery_accessory:'',
    pricefor_battery_accessory:'',
    currencycode_battery_accessory:'',
    currencysymbol_battery_accessory:'',
    currencyformat_battery_accessory:'',
    oldprice_battery_accessory:'',
    oldprice_currencycode_battery_accessory:'',
    oldprice_currencysymbol_battery_accessory:'',
    oldprice_currencyformat_battery_accessory:'',

    //for shipping
     get_shipping:'',
     shipping_props:'',
     ready_to_ship:'',
     in_stock:'',
     fast_dispatch:'',

     //shipping method
     shipping_country:'',
     shipping_title:'',
     shippingtime_leadvalue:'',
     shippingtime_info:'',

     //shipping cost
     shipping_cost_value:'',
     shipping_currencycode:'',
     shipping_currencysymbol:'',
     shipping_currencyformat:'',


    
     //product name
     product_name:'',
     product_tags:'',

     //for discount,reviews,count
     get_discount:'',
     discount_amount:'',
     discount_end_date:'',
     get_review:'',
     get_review_count:'',
     get_rating:'',
     get_total_buyers:'',

     //increase and descrease quantity
     quantity_for_1080p:0,
     quantity_for_4k:0,
     quantity_for_battery:0,

     //carttotals
     total_price:0,

     //image
     productimage:''
}


const ProductContext = React.createContext()

export const ProductProvider = ({children}) =>{

    const [state,dispatch] = useReducer(reducer,initialState)


    const homepagedummyproducts = async(homepageurl) =>{
        dispatch({
            type:PRODUCTS_BEGIN
        })
        try{
            const response = await axios.get(homepageurl)
            const homepageproduct = response.data
           //console.log(homepageproduct);
            dispatch({
                type:PRODUCTS_SUCCESS,
                payload:homepageproduct
            })
        }catch(error){
            console.log(`error is ${error.message}`);
            dispatch({
                type:PRODUCTS_ERROR
            })
        }
    }


    const fetchgivenproduct = async(url) =>{
        dispatch({
            type:SINGLE_PRODUCT_BEGIN
        })
        try{
            const response = await axios.get(url)
            const singleproduct = response.data.product
            //console.log(singleproduct);

            dispatch({
                type:GET_RATING_DETAILS,
                payload:singleproduct
            })
            //for discount
            const getdiscount = singleproduct.discount
            const discountamount = getdiscount.amount
            const discountenddate = getdiscount.end_date
            const gettags = singleproduct.tags
            console.log(discountenddate);


            

            //for reviews and rating
            const getreviews = singleproduct.reviews
            const getcount = getreviews.count
            const getrating = getreviews.rating
            const gettotalbuyers = getreviews.total_buyers
            //console.log(getcount);


            const getoptions = Object.entries(singleproduct.options)
            //console.log(getoptions);

            //const mapping = getoptions.filter((opt)=>opt.label)
            //console.log(mapping);
            //for 1080p
            const get1080p = getoptions[0]
            const get1080pvalues = get1080p[1]
            dispatch({
                type:FEATURES_1080P,
                payload:get1080pvalues
            })

            dispatch({
                type:TOTAL_PRICE,
                payload:singleproduct
            })

            dispatch({
                type:PRODUCT_IMAGE,
                payload:singleproduct
            })

        
    
            //const {label,price,old_price} = get1080pvalues
            //const labelfor1080p = get1080pvalues.label
            // const pricefor1080p = get1080pvalues.price.value
            // const currencycodefor1080p = get1080pvalues.price.currency.code
            // const currencysymbolfor1080p = get1080pvalues.price.currency.symbol
            // const currencyformatfor1080p = get1080pvalues.price.currency.format

            // //for oldprice
            // const oldpricevaluefor1080p = get1080pvalues.old_price.value
            // const oldpricecurrencycodefor1080p = get1080pvalues.old_price.currency.code
            // const oldpricecurrencysymbolfor1080p = get1080pvalues.old_price.currency.symbol
            // const oldpricecurrencyformatfor1080p = get1080pvalues.old_price.currency.format
            
            
            //for 4k

            const get4k = getoptions[1]
            const get4kvalues = get4k[1]
            
            dispatch({
                type:FEATURES_4K,
                payload:get4kvalues
            })

            // const labelfor4k = get4kvalues.label
            // const pricevaluefor4k = get4kvalues.price.value
            // const currencycodefor4k = get4kvalues.price.currency.code
            // const currencysymbolfor4k = get4kvalues.price.currency.symbol
            // const currencyformatfor4k = get4kvalues.price.currency.format
            
            // const oldpricevaluefor4k = get4kvalues.old_price.value
            // const oldpricecurrencycodefor4k = get4kvalues.old_price.currency.code
            // const oldpricecurrencysymbolfor4k = get4kvalues.old_price.currency.symbol
            // const oldpricecurrencyformatfor4k = get4kvalues.old_price.currency.format

            // console.log(labelfor4k,pricevaluefor4k,currencycodefor4k,
            //     currencysymbolfor4k,currencyformatfor4k,
            //     oldpricecurrencycodefor4k,
            //     oldpricevaluefor4k,oldpricecurrencyformatfor4k,oldpricecurrencysymbolfor4k);


            //for battery accessories 
            const getbatteryaccess = getoptions[2]
            const getbatteryaccessvalues = getbatteryaccess[1]
            
            dispatch({
                type:FEATURES_BATTERY_ACCESSORIES,
                payload:getbatteryaccessvalues
            })


            // const labelforbatteryaccess = getbatteryaccessvalues.label
            // const pricevalueforbatteryaccess = getbatteryaccessvalues.price.value
            // const currencycodeforbatteryaccess = getbatteryaccessvalues.price.currency.code
            // const currencysymbolforbatteryaccess = getbatteryaccessvalues.price.currency.symbol
            // const currencyformatforbatteryaccess = getbatteryaccessvalues.price.currency.format
            
            // const oldpricevalueforbatteryaccess = getbatteryaccessvalues.old_price.value
            // const oldpricecurrencycodeforbatteryaccess = getbatteryaccessvalues.old_price.currency.code
            // const oldpricecurrencysymbolforbatteryaccess = getbatteryaccessvalues.old_price.currency.symbol
            // const oldpricecurrencyformatforbatteryaccess = getbatteryaccessvalues.old_price.currency.format

            // console.log(labelforbatteryaccess,pricevalueforbatteryaccess,currencycodeforbatteryaccess,
            //     currencysymbolforbatteryaccess,currencyformatforbatteryaccess,oldpricevalueforbatteryaccess,
            //     oldpricecurrencycodeforbatteryaccess,oldpricecurrencysymbolforbatteryaccess,oldpricecurrencyformatforbatteryaccess
            //     );

            // dispatch({
            //     type:TOTAL_PRICE,
            //     payload:singleproduct
            // })

            const getname = singleproduct
            dispatch({
                type:GET_PRODUCT_NAME_AND_TAG,
                payload:getname
            })
            //console.log(singleproduct);
            //for shipping
            const getshipping = singleproduct.shipping
            dispatch({
                type:SHIPPING_DETAILS,
                payload:getshipping
            })
            // const shippingprops = getshipping.props
            // const readytoship = shippingprops.ready_to_ship
            // const instock = shippingprops.in_stock
            // const fastdispatch = shippingprops.fast_dispatch
           // console.log(getname);
            dispatch({
                type:SINGLE_PRODUCT_SUCCESS,
                payload:singleproduct
                // {singleproduct,getdiscount,
                //     getoptions,getreviews,getname,gettotalbuyers,
                //     getshipping,getoptions,gettags,
                //     shippingprops,readytoship,instock,
                //     fastdispatch,getcount,getrating,discountamount,discountenddate,
                //     pricefor1080p,currencycodefor1080p,currencysymbolfor1080p,currencyformatfor1080p,
                // oldpricecurrencycodefor1080p,oldpricecurrencysymbolfor1080p,oldpricecurrencyformatfor1080p,
                // oldpricevaluefor1080p,labelfor4k,pricevaluefor4k,currencycodefor4k,
                // currencysymbolfor4k,currencyformatfor4k,
                // oldpricecurrencycodefor4k,
                // oldpricevaluefor4k,oldpricecurrencyformatfor4k,oldpricecurrencysymbolfor4k,
                // labelforbatteryaccess,pricevalueforbatteryaccess,currencycodeforbatteryaccess,
                // currencysymbolforbatteryaccess,currencyformatforbatteryaccess,oldpricevalueforbatteryaccess,
                // oldpricecurrencycodeforbatteryaccess,oldpricecurrencysymbolforbatteryaccess,oldpricecurrencyformatforbatteryaccess
                // }
            })
        }catch(error){
            console.log(`error is ${error.message}`);
            dispatch({
                type:SINGLE_PRODUCT_ERROR
            })
        }
        
    }

    const increasequantityfor1080p = (quantity_for_1080p) =>{
        dispatch({
            type:INCREASE_QUANTITY,
            payload:{quantity_for_1080p}
        })
    }



    useEffect(() => {
       homepagedummyproducts(homepageurl)
    }, [])

        useEffect(() => {
           fetchgivenproduct(url)
        }, [])



    return(
        <ProductContext.Provider value={{
            ...state,
            fetchgivenproduct,
            homepagedummyproducts,
            increasequantityfor1080p,
            }}>
            {children}
        </ProductContext.Provider>
    )
}

export const useProductContext = () =>{
    return useContext(ProductContext)
}
